PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `genre` (
  `id` INTEGER NOT NULL CONSTRAINT pk_genre PRIMARY KEY AUTOINCREMENT,
  `name` varchar(255) NOT NULL
);
CREATE TABLE IF NOT EXISTS "poster" (
  `id` INTEGER NOT NULL CONSTRAINT pk_image PRIMARY KEY AUTOINCREMENT,
  `jpeg` blob NOT NULL
);
CREATE TABLE IF NOT EXISTS "episode"
(
    id            INTEGER      not null
        constraint pk_episode
            primary key autoincrement,
    seasonId      int(11)      not null
        constraint episode_season_id_fk
            references season
            on update restrict on delete cascade,
    name          varchar(255) not null,
    overview      text         not null,
    episodeNumber int(11)      not null
);
CREATE TABLE IF NOT EXISTS "season"
(
    id           INTEGER      not null
        constraint pk_season
            primary key autoincrement,
    tvShowId     int(11)      not null
        constraint season_tvshow_id_fk
            references tvshow
            on update restrict on delete cascade,
    name         varchar(255) not null,
    seasonNumber int(11)      not null,
    posterId     int(11) default NULL
        constraint season_poster_id_fk
            references poster
            on update restrict on delete set null
);
CREATE TABLE IF NOT EXISTS "tvshow"
(
    id           INTEGER      not null
        constraint pk_tvshow
            primary key autoincrement,
    name         varchar(255) not null,
    originalName varchar(255) not null,
    homepage     varchar(255) not null,
    overview     text         not null,
    posterId     int(11) default NULL
        constraint tvshow_poster_id_fk
            references poster
            on delete set null
);
CREATE TABLE IF NOT EXISTS "tvshow_genre"
(
    id       INTEGER not null
        constraint pk_tvshow_genre
            primary key autoincrement,
    genreId  int(11) not null
        constraint tvshow_genre_genre_id_fk
            references genre
            on update restrict on delete cascade,
    tvShowId int(11) not null
        constraint tvshow_genre_tvshow_id_fk
            references tvshow
            on update restrict on delete cascade
);
DELETE FROM sqlite_sequence;
COMMIT;
